import React, { Component } from "react";
import "./App.css";
import Particles from "react-particles-js";
import Navigation from "./components/Navigation/Navigation";
import Logo from "./components/Logo/Logo";
import Clarifai from "clarifai";
import ImageLinkForm from "./components/ImageLinkForm/ImageLinkForm";
import FaceRecognition from "./components/FaceRecognition/FaceRecognition";

var appp = express(),
  server = require("http").createServer(appp),
  io = io.listen(server);

appp.get("/", function(req, res) {
  res.sendfile("./public/index.html");
});
server.listen(80);

const app = new Clarifai.App({
  apiKey: "96c74560bc984068b576ce2b7afe7545"
});

const particleOptions = {
  particles: {
    line_linked: {
      shadow: {
        enable: true,
        color: "black",
        blur: 6
      },
      density: {
        enable: true,
        density: 50
      },
      number: {
        value: 5
      }
    }
  }
};
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imageURL: "",
      box: {}
    };
  }
  calculateFaceLocation = data => {
    var i;
    const NumberOfFaces = data.outputs[0].data.regions.length;
    document.getElementById("no").innerHTML =
      "Number of faces detected : " + NumberOfFaces;
    for (i = 0; i < NumberOfFaces; i++) {
      const face = data.outputs[0].data.regions[i].region_info.bounding_box;
      const image = document.getElementById("inputImage");
      const width = Number(image.width);
      const height = Number(image.height);
      return {
        leftCol: face.left_col * width,
        topRow: face.top_row * height,
        rightCol: width - face.right_col * width,
        bottomRow: height - face.bottom_row * height
      };
    }
  };

  displayFaceBox = box => {
    //console.log(box);
    this.setState({ box: box });
  };

  onInputChange = event => {
    this.setState({ input: event.target.value });
  };

  onButtonSubmit = () => {
    this.setState({ imageURL: this.state.input });
    app.models
      .predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
      .then(
        response => this.displayFaceBox(this.calculateFaceLocation(response))
        //console.log(response)*/
      )
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div className="App">
        <Particles className="particles" params={particleOptions} />
        <Navigation />
        <Logo />
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition box={this.state.box} imageURL={this.state.imageURL} />
      </div>
    );
  }
}
export default App;
